// 判断文件是否存在
func DirExist(path string) (bool, error) {
	_, err := os.Stat(path)
	if err == nil {
		return true, nil
	}
	// 判断报错是否是因为文件不存在引起的
	if os.IsNotExist(err) {
		return false, nil
	}
	return false, err
}

// 新建文件夹，必要时创建文件的父级目录
func MkDir(path string) error {
	err := os.MkdirAll(path, os.ModePerm)
	if err != nil {
		return err
	}
	return nil
}
